	    A.POST_NO
	  , A.POST_TITLE
	  , A.POST_CONTENT
	  , A.POST_REGISTRATION_DATE
      , A.POST_MODIFIED_DATE
      , A.MANAGER_MEMBER_NO
      , A.POST_EXPOSURE_STATUS 
      , A.POST_KINDS
	  )
	  VALUES
	  (
	  	SEQ_POST_NO.NEXTVAL
	  , #{ postTitle }	
	  , #{ postContent }
	  , #{ postRegistrationDate }
	  , SYSDATE
	  , #{ managerMemberNo }
	  ,#{ postExposureStatus }
	  , '공지사항'
	  )
