mapper.select:
SELECT /* com.itsme.letitgo.company.recruit.applicant.model.mapper.RecruitApplicantMapper#selectApplicant() */ A.RNUM , A.RESUME_NO , A.RESUME_TITLE , A.MEM_NAME , A.JOB_POST_BROWES_STATUS
FROM (SELECT ROWNUM RNUM , B.RESUME_NO , B.RESUME_TITLE , B.MEM_NAME , B.JOB_POST_BROWES_STATUS FROM (SELECT R.RESUME_NO , R.RESUME_TITLE , M.MEM_NAME , AH.JOB_POST_BROWES_STATUS FROM TBL_APPLYING_HISTORY AH JOIN TBL_RESUME R ON (AH.RESUME_NO = R.RESUME_NO) JOIN TBL_MEMBER M ON (R.IN_MEM_NO = M.MEM_NO) WHERE AH.JOB_POST_APPLYING_STATUS = '지원완료' AND AH.JOB_POST_NO = #{ jobPostNo } ORDER BY AH.JOB_POST_APPLYING_DATE DESC )B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) A
WHERE A.RNUM >= #{ selectCriteria.startRow }

mapper.update:
UPDATE /* com.itsme.letitgo.company.recruit.applicant.model.mapper.RecruitApplicantMapper#updateBrowesStatus() */
	   TBL_APPLYING_HISTORY A
   SET A.JOB_POST_BROWES_STATUS = '열람완료'
 WHERE A.RESUME_NO = #{ resumeNo }
   AND A.JOB_POST_NO = #{ jobPostNo }
